from functools import wraps

__author__ = 'chiesa'
__copyright__ = "Copyright 2015, Alpes Lasers SA"


def synchronized(f):
    @wraps(f)
    def wrapper(self, *args, **kargs):
        try:
            self._myLock.acquire()
            return f(self, *args, **kargs)
        finally:
            self._myLock.release()
    return wrapper
